mapper.select:
select singleSqlCfg.id, sysCfg.sys_alias, sysCfg.sys_chinese_nme, moduleCfg.module_name, moduleCfg.module_alias, tableCfg.table_name, tableCfg.table_chn_name, singleSqlCfg.remark, singleSqlCfg.single_table_sql, singleSqlCfg.create_date, singleSqlCfg.create_user, singleSqlCfg.update_date, singleSqlCfg.update_user, singleQueryWhereInfo.id as id1, singleQueryWhereInfo.single_sql_id as id2, singleQueryWhereInfo.key_info, singleQueryWhereInfo.key_chn_info from ohs_single_sql_config singleSqlCfg inner join ohs_sys_config sysCfg on sysCfg.id = singleSqlCfg.sys_id
and sysCfg.sys_alias = #{sysAlias}
and sysCfg.sys_chinese_nme = #{sysChineseNme}
inner join ohs_module_config moduleCfg on moduleCfg.id = singleSqlCfg.module_id
and moduleCfg.module_alias = #{moduleAlias}
and moduleCfg.module_name = #{moduleName}
inner join ohs_table_config tableCfg on tableCfg.id = singleSqlCfg.table_id
and tableCfg.table_name = #{tableName}
and tableCfg.table_chn_name = #{tableChnName}
left join ohs_single_query_where_info singleQueryWhereInfo on singleQueryWhereInfo.single_sql_id = singleSqlCfg.id where 1 = 1
and singleSqlCfg.remark = #{remark}
and singleSqlCfg.single_table_sql = #{singleTableSql}
limit #{pageSize} offset #{offsetSize}

select count(1) from ohs_single_sql_config singleSqlCfg inner join ohs_sys_config sysCfg on sysCfg.id = singleSqlCfg.sys_id
and sysCfg.sys_alias = #{sysAlias}
and sysCfg.sys_chinese_nme = #{sysChineseNme}
inner join ohs_module_config moduleCfg on moduleCfg.id = singleSqlCfg.module_id
and moduleCfg.module_alias = #{moduleAlias}
and moduleCfg.module_name = #{moduleName}
inner join ohs_table_config tableCfg on tableCfg.id = singleSqlCfg.table_id
and tableCfg.table_name = #{tableName}
and tableCfg.table_chn_name = #{tableChnName}
left join ohs_single_query_where_info singleQueryWhereInfo on singleQueryWhereInfo.single_sql_id = singleSqlCfg.id where 1 = 1
and singleSqlCfg.remark = #{remark}
and singleSqlCfg.single_table_sql = #{singleTableSql}
